import React, { useState, useEffect, Fragment } from 'react'
import { Table, TableHead, TableCell, TableRow, TableBody, Button, makeStyles } from '@material-ui/core';
import { getProducts, deleteProduct } from '../../services/ProductService';
import { Link } from 'react-router-dom';
import { NavLink } from 'react-router-dom';

const useStyles = makeStyles({
    table: {
        width: '90%',
        margin: '50px 0 0 50px'
    },
    thead: {
        '& > *': {
            fontSize: 17,
            background: '#2bb4c8',
            color: '#FFFFFF',
            family: 'Nunito,Helvetica,Arial,sans-serif'
        }
    },
    row: {
        '& > *': {
            fontSize: 15,
            family: 'Nunito,Helvetica,Arial,sans-serif'
        }
    }
})

export function ProductList() {
    const classes = useStyles();

    const [products, setProducts] = useState([])

    useEffect(() => {
        getAllProducts();
    }, [])

    const getAllProducts = async () => {
        let response = await getProducts();
        console.log(response);
        setProducts(response.data.data);
    }

    const deleteProductData = async (id) => {
        let callbackUser = window.confirm('Esta seguro de elimar el prudcto');
        if (callbackUser) {
            await deleteProduct(id);
            getAllProducts();
        }
    }

    return (
        <Table className={classes.table}>
            <TableHead>
                <TableRow className={classes.thead}>
                    <TableCell>Id Product</TableCell>
                    <TableCell>Description</TableCell>
                    <TableCell>Value</TableCell>
                    <TableCell>State</TableCell>
                    <TableCell></TableCell>
                </TableRow>
            </TableHead>
            <TableBody>
                {
                    products.map(product => (
                        <TableRow className={classes.row} key={product._id}>
                            <TableCell>{product._id}</TableCell>
                            <TableCell>{product.description}</TableCell>
                            <TableCell>{product.value}</TableCell>
                            <TableCell>{product.state ? "In-Stock" : "Out-of-stock"}</TableCell>
                            <TableCell>
                                <Button component={Link} to={`/edit/${product._id}`} color="primary">Editar</Button>
                                <Button color="secondary" onClick={() => deleteProductData(product._id)} >Eliminar</Button>
                            </TableCell>
                        </TableRow>
                    ))
                }
            </TableBody>
            
            <div className="Buttons">
                <NavLink to="/"> 
                    <Button title="Back" />
                </NavLink>
            </div>
            
        </Table>
        
    )
}
